import React, { useState, useEffect } from 'react';
import SearchBar from './components/SearchBar';
import GameList from './components/GameList';
import AddGameForm from './components/AddGameForm';
import './App.css';

function App() {
  const [games, setGames] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [showAddForm, setShowAddForm] = useState(false);

  // Fetch all games from the database
  const fetchGames = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/boardgames');
      if (!response.ok) {
        throw new Error('Failed to fetch games');
      }
      const data = await response.json();
      setGames(data);
    } catch (error) {
      console.error('Error fetching games:', error);
    }
  };

  // Load games when component mounts
  useEffect(() => {
    fetchGames();
  }, []);

  // Handle search
  const handleSearch = async (term) => {
    setSearchTerm(term);
    try {
      const response = await fetch(`http://localhost:5000/api/boardgames/search?term=${encodeURIComponent(term)}`);
      if (!response.ok) {
        throw new Error('Failed to search games');
      }
      const data = await response.json();
      setGames(data);
    } catch (error) {
      console.error('Error searching games:', error);
    }
  };

  // Handle adding new game
  const handleAddGame = async (newGame) => {
    try {
      const response = await fetch('http://localhost:5000/api/boardgames', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          name: newGame.title,
          description: newGame.description || '',
          min_players: parseInt(newGame.players.split('-')[0]),
          max_players: parseInt(newGame.players.split('-')[1]),
          playing_time: parseInt(newGame.time.split('-')[0]),
          complexity: newGame.complexity,
          image_url: newGame.image,
          dateAdded: new Date().toISOString().split('T')[0]
        })
      });

      if (!response.ok) {
        throw new Error('Failed to add game');
      }

      // Refresh the games list after adding
      await fetchGames();
      setShowAddForm(false);
    } catch (error) {
      console.error('Error adding game:', error);
      alert('Failed to add game. Please try again.');
    }
  };

  return (
    <div className="container">
      <h1>Boardgame Database</h1>
      <div className="controls">
        <SearchBar onSearch={handleSearch} />
        <button 
          className="add-button"
          onClick={() => setShowAddForm(true)}
        >
          Add Game
        </button>
      </div>

      {showAddForm && (
        <div className="modal">
          <div className="modal-content">
            <AddGameForm 
              onSubmit={handleAddGame}
              onCancel={() => setShowAddForm(false)}
            />
          </div>
        </div>
      )}

      <GameList games={games} />
    </div>
  );
}

export default App;
